mapper.update:
UPDATE activity
SET state = #{afterState}
WHERE activity_id = #{activityId} AND state = #{beforeState}

mapper.select:
SELECT activity_id, activity_name, area, passwd, state, creator, create_time, update_time
FROM activity
WHERE activity_id = #{activityId}

SELECT count(*) FROM activity

    
        activity_id = #{activityId}
    
    
        activity_name = #{activityName}

SELECT id, activity_id, activity_name, area, passwd, state, creator, create_time, update_time
FROM activity


    
        activity_id = #{activityId}
    
    
        activity_name = #{activityName}
    

ORDER BY id DESC

LIMIT #{pageBegin}, #{pageEnd}
